window.onload = function () {
    window.addEventListener('scroll', function (e) {
        if (window.pageYOffset > 100) {
            document.querySelector('header').classList.add('is-scrolling');
        } else {
            document.querySelector('header').classList.add('is-scrolling');
        }
    });
}
